import { createContext, useState } from "react";
import SpecialHeader from "../components/Common-components/SpecialHeader";
import CategoryIpads from "../components/Common-components/CategoryIpads";
import Skeleton from "../components/Skeleton/Skeleton";
import ProductCard from "../components/ProductCard";

const DiagonalContext = createContext();

function Ipads() {
  const [data, setData] = useState([]);
  const url = "http://localhost:8080/product";
  const [isLoading, setIsLoading] = useState(false);
  const [page, setPage] = useState(0);
  const [size, setSize] = useState(9);
  const [totalPages, setTotalPages] = useState(0);
  const [model, setModel] = useState([]);
  const [memory, setMemory] = useState([]);
  const [price, setPrice] = useState("");
  const [diagonal, setDiagonal] = useState("");

  return (
    <>
      <SpecialHeader></SpecialHeader>
      <div className="mainPhoneContainer">
        <div className="phoneContainer">
          <div className="categoryIphones">
            <ModelContext.Provider  value={{model, setModel}}>
              <MemoryContext.Provider value={{memory, setMemory}}>
                <PriceContext.Provider value={{price, setPrice}}>
                  <DiagonalContext.Provider value={{diagonal, setDiagonal}}>
                    <CategoryIpads></CategoryIpads>
                  </DiagonalContext.Provider>
                </PriceContext.Provider>
              </MemoryContext.Provider>
            </ModelContext.Provider>
          </div>
          <div className="iphoneSection">
            <div className="iphoneContainer">
              {isLoading ? [... new Array(6)].map((_, index) => <Skeleton></Skeleton>) : data != null ? data.map((obj) => <ProductCard key={obj.id} {...obj}></ProductCard>) : null}
            </div>
            <div className="pagination">
              {totalPages !== 0 ? [...new Array(totalPages)].map((obj, index) => <button onClick={() => setPage(index)}>{index + 1}</button>) : null}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Ipads;
