import { configureStore } from "@reduxjs/toolkit";
import { modelReducer, memoryReducer, priceReducer, diagonalReducer, tokenReducer, pageReducer, sizeReducer } from "./SpecificationSlice";
import { apiAll } from "./api";

const store = configureStore({
    reducer: {
        model: modelReducer,
        memory: memoryReducer,
        price: priceReducer,
        diagonal: diagonalReducer,
        token: tokenReducer,
        page: pageReducer,
        size: sizeReducer,
        [apiAll.reducerPath]: apiAll.reducer,
    },
    middleware: (getDefaultMiddlware) => getDefaultMiddlware().concat(apiAll.middleware)
});

export default store;
