import "./App.css";
import "./styles/style.scss";

import Footer from "./components/Common-components/Footer";
import Home from "./pages/Home";
import Login from "./components/Login-page/Login";
import Register from "./components/Register-page/Register";
import NotFound from "./pages/NotFound";
import Iphone from "./pages/Iphones";
import Ipad from "./pages/Ipads";
import Mac from "./pages/Macs";
import Applewatch from "./pages/Applewatches";

import { Route, Routes } from "react-router-dom";
import { createContext, useState } from "react";
import Profile from "./pages/Profile";
import ProductPage from "./components/Product-page/Product";

export const ModelContext = createContext();
export const MemoryContext = createContext();
export const PriceContext = createContext();
export const DiagonalContext = createContext();

function App() {
  const [model, setModel] = useState([]);
  const [memory, setMemory] = useState([]);
  const [price, setPrice] = useState("");
  const [diagonal, setDiagonal] = useState("");

  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [totalPages, setTotalPages] = useState(0);
  const [page, setPage] = useState(0);
  const [size, setSize] = useState(9);

  useEffect(() => {
    setIsLoading(true);
    fetch(url + 
        "/all?page=" + page + 
        "&size=" + size)
    .then((res) => {
        return res.json();
    })
    .then((array) => {
        setData(array.content);
        setIsLoading(false);
        setTotalPages(array.totalPages);
    })
}, [page, size]);

useEffect(() => {
    setIsLoading(true);
    fetch(url + 
        "/category?page=" + page + 
        "&size=" + size + 
        "&model=" + model + 
        "&memory=" + memory + 
        "&price=" + price + 
        "&diagonal=" + diagonal)
    .then((res) => {
        return res.json();
    })
    .then((array) => {
        setData(array.content);
        setIsLoading(false);
        setTotalPages(array.totalPages);
    })
}, [page, size, memory, model, price, diagonal]);

  return (
    <>
      <Routes>
        <Route path="/" element={<Home></Home>}></Route>
        <Route path="/login" element={<Login></Login>}></Route>
        <Route path="/register" element={<Register></Register>}></Route>
        <Route path="*" element={<NotFound></NotFound>}></Route>
        <Route path="/iPhones" element={<Iphone></Iphone>}></Route>
        <Route path="/iPads" element={<Ipad></Ipad>}></Route>
        <Route path="/macs" element={<Mac></Mac>}></Route>
        <Route path="/applewatches" element={<Applewatch></Applewatch>}></Route>
        <Route path="/profile" element={<Profile></Profile>}></Route>
        <Route path="/product" element={<ProductPage></ProductPage>}></Route>
      </Routes>
      <Footer></Footer>
    </>
  );
}

export default App;
