import { useState, useEffect, createContext } from "react";
import Iphones from "./Iphones";
import Ipads from "./Ipads";

export const ModelContext = createContext();
export const MemoryContext = createContext();
export const PriceContext = createContext();
export const DiagonalContext = createContext();

function ServerSending() {
    const url = "http://localhost:8080/product";

    const [model, setModel] = useState([]);
    const [memory, setMemory] = useState([]);
    const [price, setPrice] = useState("");
    const [diagonal, setDiagonal] = useState("");

    const [data, setData] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [totalPages, setTotalPages] = useState(0);
    const [page, setPage] = useState(0);
    const [size, setSize] = useState(9);

    useEffect(() => {
        setIsLoading(true);
        fetch(url + 
            "/all?page=" + page + 
            "&size=" + size)
        .then((res) => {
            return res.json();
        })
        .then((array) => {
            setData(array.content);
            setIsLoading(false);
            setTotalPages(array.totalPages);
        })
    }, [page, size]);

    useEffect(() => {
        setIsLoading(true);
        fetch(url + 
            "/category?page=" + page + 
            "&size=" + size + 
            "&model=" + model + 
            "&memory=" + memory + 
            "&price=" + price + 
            "&diagonal=" + diagonal)
        .then((res) => {
            return res.json();
        })
        .then((array) => {
            setData(array.content);
            setIsLoading(false);
            setTotalPages(array.totalPages);
        })
    }, [page, size, memory, model, price, diagonal]);

    return (
        <>
            <ModelContext.Provider value={{model, setModel}}>
                <MemoryContext.Provider value={{memory, setMemory}}>
                    <PriceContext.Provider value={{price, setPrice}}>
                        <DiagonalContext.Provider value={{diagonal, setDiagonal}}>
                            <Iphones 
                            data={data} 
                            isLoading={isLoading} 
                            totalPages={totalPages} 
                            onCLickPage={(page) => setPage(page)} 
                            onClickSize={(size) => setSize(size)}>
                            </Iphones>
                            <Ipads
                            data={data} 
                            isLoading={isLoading} 
                            totalPages={totalPages} 
                            onCLickPage={(page) => setPage(page)} 
                            onClickSize={(size) => setSize(size)}>
                                <CategoryIpads></CategoryIpads>
                            </Ipads>
                        </DiagonalContext.Provider>
                    </PriceContext.Provider>
                </MemoryContext.Provider>
            </ModelContext.Provider>
        </>
    )
}
