import { useRef, useState } from "react";
import { Link, Navigate, useNavigate } from "react-router-dom";
import SpecialHeader from "../Common-components/SpecialHeader";
import { loginUser } from "../Services/Services";

function Login() {
  const [isChecked, setIsCheked] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const loginRef = useRef(null);

  const handleCheck = (event) => {
    setIsCheked(event.target.checked);

    // if (!isChecked && loginRef.current.classList.contains("active")) {
    //     loginRef.current.
    // }
    if (isChecked == false && !document.querySelector("#login").classList.contains("active")) {
      document.querySelector("#login").classList.add("active");
      document.querySelector("#login").classList.remove("notActive");
    } else if (isChecked == true) {
      document.querySelector("#login").classList.remove("active");
      document.querySelector("#login").classList.add("notActive");
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (isChecked) {
      try {
        const token = await loginUser({ email, password });
        localStorage.setItem("token", token);
        // useNavigate("/profile");
      } catch (error) {
        setError(error);
      }
    }
  };

  return (
    <>
      <SpecialHeader></SpecialHeader>

      <div className="loginForm">
        <div className="loginWrapper">
          <h1>Log In</h1>
          <form onSubmit={handleSubmit}>
            {error && <p>{error}</p>}
            <input
              type="email"
              placeholder="Email"
              onChange={(event) => setEmail(event.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              required
              onChange={(event) => setPassword(event.target.value)}
            />
            <div>
              <label htmlFor="checkBox">Agree to our terms</label>
              <input
                type="checkBox"
                name="checkBox"
                className="termsCheckbox"
                onChange={handleCheck}
              />
            </div>
            {!isChecked && <p id="notCheked">You must agree to our terms.</p>}
            <input
              ref={loginRef}
              type="submit"
              value="Log In"
              id="login"
              className="notActive"
              disabled={!isChecked}
            />
          </form>
          <p>
            Don't have an account? <Link to="/register">Register</Link>
          </p>
        </div>
      </div>
    </>
  );
}

export default Login;
