import { createApi, fetchBaseQuery } from "@reduxjs/toolkit/query/react";
import { useState } from "react";

export const api = createApi({
  reducerPath: "api",
  tagTypes: ["TechProducts"],
  baseQuery: fetchBaseQuery({ baseUrl: "http://localhost:8080/product" }),
  endpoints: (build) => ({
    getAllProducts: build.query({
      query: (page = 0, size = 9, token = "") =>
        `/all?page=${page}&size=${size}&token=${token}`,
      providesTags: (result) =>
        result?.co
          ? [
              ...result.content.map(({ id }) => ({ type: "TechProducts", id })),
              { type: "TechProducts", id: "LIST" },
            ]
          : [{ type: "TechProducts", id: "LIST" }],
    }),
  }),
});

export const {useGetAllProductsQuery } = api;
