import { useState } from "react";
import { useEffect } from "react";

function MainAnimation() {
  const baseText = "Best";
  const words = ["Devices", "Prices", "Quality"];
  const typingSpeed = 200;
  const deletingSpeed = 150;
  const pauseDuration = 1500;

  const [text, setText] = useState(baseText);
  const [isDeleting, setIsDeleting] = useState(false);
  const [typingInterval, setTypingInterval] = useState(0);
  const [wordIndex, setWordIndex] = useState(0);

  useEffect(() => {
    let currentWord = words[wordIndex];

    const textAppearance = () => {
      if (text == baseText + currentWord && !isDeleting) {
        setTimeout(() => setIsDeleting(true), pauseDuration);
      } else if (isDeleting && text == baseText) {
        isDeleting(false);
        setWordIndex((previousIndex) => (previousIndex + 1) % words.length);
        setTypingInterval(typingSpeed);
      } else if (isDeleting) {
        setTypingInterval(deletingSpeed);
        setText(baseText + currentWord.substring(0, text.length - baseText.length - 1));
      } else if (!isDeleting) {
        setText(baseText + currentWord.substring(0, text.length - baseText.length + 1));
      }
    };

    setTimeout(() => textAppearance, typingInterval);

    return clearTimeout(textAppearance);
  }, [text, wordIndex, isDeleting]);
}

export default MainAnimation;
