import { useEffect, useLocation, useState  } from "react";
import { useNavigate } from "react-router-dom";

export default function Verification() {
    const retrieveingProcess = () => {
        return new URLSearchParams(useLocation().search);
    }
    const url = "http://localhost:8080/shop/verify/";
    const [message, setMessage] = useState("");
    const navigate = useNavigate();

    useEffect(async () => {
        let token = retrieveingProcess.get("token");
        try {
            const response = await fetch(url + "?token=" + token);
            setMessage(response.text());
            setTimeout(() => {
                navigate("/login");
            }, 3000);
        } catch (error) {
            setMessage("Verification failed! Invalid or expired token.");
        }
    }, [retrieveingProcess]);

    return (
        <div>
            <h2>Email Verification</h2>
            <p>{message}</p>
        </div>
    )
}
