import { useContext, useState } from "react"
import { DiagonalContext, MemoryContext, ModelContext, PriceContext } from "../../pages/ServerSending";

function CategoryIpads() {
    const {diagonal, setDiagonal} = useContext(DiagonalContext);
    const {model, setModel} = useContext(ModelContext);
    const {price, setPrice} = useContext(PriceContext);
    const {memory, setMemory} = useContext(MemoryContext);

    const [selectedModel, setSelectedModel] = useState(false);
    const [memoryClick, memoryHeaderClick] = useState(false);
    const [sortClick, setSortCLick] = useState(false);
    const [cheked, setCheked] = useState(null);
    const [models, setModels] = useState([]);
    const [memories, setMemories] = useState([]);

    const [selectedDiagonal, setSelectedDiagonal] = useState([]);

    const handleModel = (event) => {
        if (event.target.checked) {
            setModels((prevModels) => {
                const updatedModels = [...prevModels, event.target.value];
                setModel(updatedModels);
                return updatedModels;
            });
        } else {
            setModels((prevModels) => {
                const updatedModels = prevModels.filter((model) => model !== event.target.value);
                setModel(updatedModels); 
                return updatedModels;
            }); 
        }
    }

    const handleMemory = (event) => {
        if (event.target.checked) {
            setMemories((prevMemory) => {
                const updatedMemory = [...prevMemory, event.target.value];
                setMemory(updatedMemory);
                return updatedMemory;
            });
        } else {
            setMemories((prevMemory) => {
                const updatedMemory = prevMemory.filter((memory) => memory !== event.target.value);
                setMemory(updatedMemory); 
                return updatedMemory;
            }); 
        }
    }
    
    const handlePrice = (event) => {
        setCheked(() => {
            setPrice(event.target.value)
            return event.target.value;
        })
    }

    const handleDiagonal = (event) => {
        if (event.target.checked) {
            setSelectedDiagonal((prevDiagonal) => {
                const newDiagonal = [...prevDiagonal, event.target.value];
                setDiagonal(newDiagonal);
                return newDiagonal;
            })
        } else {
            setSelectedDiagonal((prevDiagonal) => {
                const newDiagonal = prevDiagonal.filter((diagonal) => diagonal !== event.target.value);
                setDiagonal(newDiagonal);
                return newDiagonal;
            })
        }
    }

    return (
        <div className="parent">
            <div className="item">
                <div className="item-header" onClick={() => setModelClick(!modelCLick)}>
                    <h3>Model</h3>
                    {!modelCLick && (<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="lucide lucide-chevron-up"><path d="m18 15-6-6-6 6"/></svg>)}
                    {modelCLick && (<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="lucide lucide-chevron-down"><path d="m6 9 6 6 6-6"/></svg>)}
                </div>
                {modelCLick && (
                    <div className="categoryChecks">
                        <div className="categoryChecksCildren">
                            <input type="checkBox" name="mini" value="iPadMini" onChange={handleModel} />
                            <label htmlFor="pro">iPad mini</label>
                        </div>
                        <div className="categoryChecksCildren">
                            <input type="checkBox" name="iPad Pro" value="iPadPro" onChange={handleModel} />
                            <label htmlFor="modelType">iPad Pro</label>
                        </div>
                        <div className="categoryChecksCildren">
                            <input type="checkBox" name="regular" value="iPadAir" onChange={handleModel} />
                            <label htmlFor="modelType">iPad Air</label>
                        </div>
                    </div>
                )}
            </div>
            <div className="item">
                <div className="item-header" onClick={() => memoryHeaderClick(!memoryClick)}>
                    <h3>Memory</h3>
                    {!memoryClick && (<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="lucide lucide-chevron-up"><path d="m18 15-6-6-6 6"/></svg>)}
                    {memoryClick && (<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="lucide lucide-chevron-down"><path d="m6 9 6 6 6-6"/></svg>)}
                </div>
                {memoryClick && (
                    <div className="categoryChecks">
                        <div className="categoryChecksCildren">
                            <input type="checkBox" name="smallMemory" value="128" onChange={handleMemory} />
                            <label htmlFor="smallMemory">128 GB</label>
                        </div>
                        <div className="categoryChecksCildren">
                            <input type="checkBox" name="normalMemory" value="256" onChange={handleMemory} />
                            <label htmlFor="normalMemory">256 GB</label>
                        </div>
                        <div className="categoryChecksCildren">
                            <input type="checkBox" name="largeMemory" value="512" onChange={handleMemory} />
                            <label htmlFor="largeMemory">512 GB</label>
                        </div>
                        <div className="categoryChecksCildren">
                            <input type="checkBox" name="superLarge" value="1024" onChange={handleMemory} />
                            <label htmlFor="superLarge">1 TB</label>
                        </div>
                    </div>
                )}
            </div>
            <div className="item">
                <div className="item-header" onClick={() => setSortCLick(!sortClick)}>
                    <h3>Sort</h3>
                    {!sortClick && (<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="lucide lucide-chevron-up"><path d="m18 15-6-6-6 6"/></svg>)}
                    {sortClick && (<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="lucide lucide-chevron-down"><path d="m6 9 6 6 6-6"/></svg>)}
                </div>
                {sortClick && (
                    <div className="categoryChecks">
                        <div className="categoryChecksCildren">
                            <input type="checkBox" name="highLow" value="desc" checked={cheked === "desc"} onChange={handlePrice} />
                            <label htmlFor="highLow">High-Low</label>
                        </div>
                        <div className="categoryChecksCildren">
                            <input type="checkBox" name="lowHigh" value="asc" checked={cheked === "asc"} onChange={handlePrice} />
                            <label htmlFor="lowHigh">Low-High</label>
                        </div>
                    </div>
                )}
            </div>
            <div className="item">
                <div className="item-header" onClick={setSortCLick(!sortClick)}>
                    <h3>Diagonal</h3>
                    {!sortClick && (<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="lucide lucide-chevron-up"><path d="m18 15-6-6-6 6"/></svg>)}
                    {sortClick && (<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="lucide lucide-chevron-down"><path d="m6 9 6 6 6-6"/></svg>)}
                </div>
                {sortClick && (
                    <div className="categoryChecks">
                        <div className="categoryChecksCildren">
                            <input type="checkBox" name="8.3inch" value="8.3inch" onChange={handleDiagonal} />
                            <label htmlFor="8.3inch">8.3 inch</label>
                        </div>
                        <div className="categoryChecksCildren">
                            <input type="checkBox" name="11" value="11" onChange={handleDiagonal} />
                            <label htmlFor="11">11 inch</label>
                        </div>
                        <div className="categoryChecksCildren">
                            <input type="checkBox" name="13" value="13" onChange={handleDiagonal} />
                            <label htmlFor="13">13 inch</label>
                        </div>
                    </div>
                )}
            </div>
        </div>  
    )
}

export default CategoryIpads;
