import Category from "../components/Common-components/Category";
import SpecialHeader from "../components/Common-components/SpecialHeader";
import ProductCard from "../components/ProductCard";
import { useEffect, useState, useContext, createContext } from "react";
import Skeleton from "../components/Skeleton/Skeleton";

export const ModelContext = createContext();

function Iphones() {
  const [data, setData] = useState([]);
  const url = "http://localhost:8080/product";
  const [isLoading, setIsLoading] = useState(false);
  const [page, setPage] = useState(0);
  const [size, setSize] = useState(9);
  const [totalPages, setTotalPages] = useState(0);
  const [model, setModel] = useState([]);
  const [memory, setMemory] = useState([]);
  const [price, setPrice] = useState("");

  console.log(model);

  useEffect(() => {
            setIsLoading(true);
            fetch(url + "/all?page=" + page + "&size=" + size)
            .then((res) => {
              return res.json();
            })
            .then((array) => {
              setData(array.content);
              setIsLoading(false);
              setTotalPages(array.totalPages);
            });
          }, [page, size]
  );

  useEffect(() => {
    setIsLoading(true);
    fetch(url + 
      "/category?page=" + page + 
      "&size=" + size + 
      "&model=" + model + 
      "&memory=" + memory +
      "&price=" + price
    )
    .then((res) => {
      res.json();
    })
    .then((array) => {
      setData(array.content);
      setIsLoading(false);
      setTotalPages(array.totalPages);
    })
  }, [page, size, price, model, memory])

  return (
    <>
      <SpecialHeader></SpecialHeader>
      <div className="mainPhoneContainer">
        <div className="phoneContainer">
          <div className="categoryIphones">
            <ModelContext.Provider  value={{model, setModel}}>
              <Category></Category>
            </ModelContext.Provider>
          </div>
          <div className="iphoneSection">
            <div className="iphoneContainer">
              {isLoading ? [... new Array(6)].map((_, index) => <Skeleton></Skeleton>) : data != null ? data.map((obj) => <ProductCard key={obj.id} {...obj}></ProductCard>) : null}
            </div>
            <div className="pagination">
              {totalPages !== 0 ? [...new Array(totalPages)].map((obj, index) => <button onClick={() => setPage(index)}>{index + 1}</button>) : null}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Iphones;
